import React from 'react';

/**
 * Minimalist Thin-Line Icons (SVG)
 * Style: 1.5px stroke, rounded caps/joins
 */

const IconBase = ({ children, size = 20, className = "" }) => (
    <svg
        width={size}
        height={size}
        viewBox="0 0 24 24"
        fill="none"
        stroke="currentColor"
        strokeWidth="1.5"
        strokeLinecap="round"
        strokeLinejoin="round"
        className={`icon-svg ${className}`}
    >
        {children}
    </svg>
);

